const express = require('express');
const router = express.Router();
const accountRecoveryService = require('../services/accountRecoveryService');
const passwordResetService = require('../services/passwordResetService');
const { authenticateToken } = require('../middleware/auth');
const rateLimit = require('express-rate-limit');

// Rate limiting for recovery requests
const recoveryRateLimit = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 5, // limit each IP to 5 requests per windowMs
  message: {
    success: false,
    error: 'Too many recovery requests. Please try again later.',
    retryAfter: 15 * 60 // seconds
  },
  standardHeaders: true,
  legacyHeaders: false
});

// Rate limiting for token verification
const verifyRateLimit = rateLimit({
  windowMs: 5 * 60 * 1000, // 5 minutes
  max: 10, // limit each IP to 10 requests per windowMs
  message: {
    success: false,
    error: 'Too many verification attempts. Please try again later.'
  }
});

// POST /api/account-recovery/initiate
// Initiate account recovery process
router.post('/initiate', recoveryRateLimit, async (req, res) => {
  try {
    const { email, recoveryType, recoveryData } = req.body;
    const ipAddress = req.ip || req.connection.remoteAddress;
    const userAgent = req.get('User-Agent');

    if (!email || !recoveryType) {
      return res.status(400).json({
        success: false,
        error: 'Email and recovery type are required'
      });
    }

    // Validate recovery type
    const validTypes = ['email_change', 'account_recovery', 'emergency_access', 'account_lockout'];
    if (!validTypes.includes(recoveryType)) {
      return res.status(400).json({
        success: false,
        error: 'Invalid recovery type'
      });
    }

    const result = await accountRecoveryService.initiateAccountRecovery(
      email,
      recoveryType,
      recoveryData || {},
      ipAddress,
      userAgent
    );

    res.json(result);
  } catch (error) {
    console.error('Recovery initiation error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to initiate account recovery'
    });
  }
});

// POST /api/account-recovery/verify-token
// Verify recovery token
router.post('/verify-token', verifyRateLimit, async (req, res) => {
  try {
    const { token } = req.body;

    if (!token) {
      return res.status(400).json({
        valid: false,
        error: 'Recovery token is required'
      });
    }

    const result = await accountRecoveryService.verifyRecoveryToken(token);
    res.json(result);
  } catch (error) {
    console.error('Token verification error:', error);
    res.status(500).json({
      valid: false,
      error: 'Failed to verify recovery token'
    });
  }
});

// POST /api/account-recovery/complete
// Complete account recovery
router.post('/complete', async (req, res) => {
  try {
    const { token, recoveryActions } = req.body;
    const ipAddress = req.ip || req.connection.remoteAddress;
    const userAgent = req.get('User-Agent');

    if (!token) {
      return res.status(400).json({
        success: false,
        error: 'Recovery token is required'
      });
    }

    if (!recoveryActions || typeof recoveryActions !== 'object') {
      return res.status(400).json({
        success: false,
        error: 'Recovery actions are required'
      });
    }

    const result = await accountRecoveryService.completeAccountRecovery(
      token,
      recoveryActions,
      ipAddress,
      userAgent
    );

    res.json(result);
  } catch (error) {
    console.error('Recovery completion error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to complete account recovery'
    });
  }
});

// POST /api/account-recovery/check-lockout
// Check account lockout status
router.post('/check-lockout', async (req, res) => {
  try {
    const { email } = req.body;

    if (!email) {
      return res.status(400).json({
        success: false,
        error: 'Email is required'
      });
    }

    const result = await passwordResetService.checkAccountLockout(email);
    res.json({
      success: true,
      ...result
    });
  } catch (error) {
    console.error('Lockout check error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to check account lockout status'
    });
  }
});

// POST /api/account-recovery/unlock-account
// Unlock account (admin or recovery process)
router.post('/unlock-account', authenticateToken, async (req, res) => {
  try {
    const { userId, reason } = req.body;
    const adminId = req.user.id;

    if (!userId) {
      return res.status(400).json({
        success: false,
        error: 'User ID is required'
      });
    }

    // Check if user has admin privileges or is unlocking their own account
    if (req.user.role !== 'admin' && req.user.id !== userId) {
      return res.status(403).json({
        success: false,
        error: 'Insufficient permissions'
      });
    }

    const result = await passwordResetService.unlockAccount(
      userId,
      reason || 'manual_unlock',
      adminId
    );

    res.json(result);
  } catch (error) {
    console.error('Account unlock error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to unlock account'
    });
  }
});

// GET /api/account-recovery/backup-codes
// Generate backup codes for account recovery
router.get('/backup-codes', authenticateToken, async (req, res) => {
  try {
    const userId = req.user.id;
    const codes = await accountRecoveryService.generateBackupCodes(10);

    // Store encrypted backup codes in database
    const backupResult = await accountRecoveryService.createCredentialBackup(
      userId,
      'backup_codes',
      { codes, generatedAt: new Date().toISOString() }
    );

    if (backupResult.success) {
      res.json({
        success: true,
        codes,
        message: 'Backup codes generated successfully. Store these in a safe place.',
        backupId: backupResult.backupId
      });
    } else {
      throw new Error('Failed to store backup codes');
    }
  } catch (error) {
    console.error('Backup codes generation error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to generate backup codes'
    });
  }
});

// POST /api/account-recovery/verify-backup-code
// Verify backup code for recovery
router.post('/verify-backup-code', async (req, res) => {
  try {
    const { email, backupCode } = req.body;

    if (!email || !backupCode) {
      return res.status(400).json({
        success: false,
        error: 'Email and backup code are required'
      });
    }

    // This would need to be implemented in the account recovery service
    // For now, return a placeholder response
    res.json({
      success: false,
      error: 'Backup code verification not yet implemented'
    });
  } catch (error) {
    console.error('Backup code verification error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to verify backup code'
    });
  }
});

// GET /api/account-recovery/security-log/:userId
// Get security audit log for user (admin or self)
router.get('/security-log/:userId', authenticateToken, async (req, res) => {
  try {
    const { userId } = req.params;
    const { limit = 50, offset = 0 } = req.query;

    // Check permissions
    if (req.user.role !== 'admin' && req.user.id !== userId) {
      return res.status(403).json({
        success: false,
        error: 'Insufficient permissions'
      });
    }

    // This would query the security_audit_log table
    // For now, return a placeholder response
    res.json({
      success: true,
      logs: [],
      total: 0,
      message: 'Security log retrieval not yet implemented'
    });
  } catch (error) {
    console.error('Security log retrieval error:', error);
    res.status(500).json({
      success: false,
      error: 'Failed to retrieve security log'
    });
  }
});

module.exports = router;

#!/usr/bin/env node

/**
 * Comprehensive Code Quality & Security Check Script
 * Automates ESLint, Prettier, security audits, and generates reports
 */

const { execSync, spawn } = require('child_process');
const fs = require('fs');
const path = require('path');
const colors = require('colors');

// Configuration
const CONFIG = {
  reportsDir: 'reports',
  eslintConfig: '.eslintrc.js',
  prettierConfig: '.prettierrc.js',
  maxIssues: {
    eslint: 50,
    security: 5,
    coverage: 80
  }
};

// Utility functions
const log = (message, type = 'info') => {
  const timestamp = new Date().toISOString();
  switch (type) {
    case 'success':
      console.log(`[${timestamp}]  ${message}`.green);
      break;
    case 'error':
      console.log(`[${timestamp}]  ${message}`.red);
      break;
    case 'warning':
      console.log(`[${timestamp}]   ${message}`.yellow);
      break;
    case 'info':
    default:
      console.log(`[${timestamp}]   ${message}`.blue);
      break;
  }
};

const runCommand = (command, options = {}) => {
  try {
    const result = execSync(command, { 
      encoding: 'utf8', 
      stdio: options.silent ? 'pipe' : 'inherit',
      ...options 
    });
    return { success: true, output: result };
  } catch (error) {
    return { 
      success: false, 
      error: error.message, 
      output: error.stdout || error.stderr || '' 
    };
  }
};

const ensureReportsDir = () => {
  if (!fs.existsSync(CONFIG.reportsDir)) {
    fs.mkdirSync(CONFIG.reportsDir, { recursive: true });
    log(`Created reports directory: ${CONFIG.reportsDir}`, 'info');
  }
};

const generateTimestamp = () => {
  return new Date().toISOString().replace(/[:.]/g, '-');
};

// Quality check functions
const runESLintCheck = () => {
  log('Running ESLint analysis...', 'info');

  const timestamp = generateTimestamp();
  const reportFile = path.join(CONFIG.reportsDir, `eslint-report-${timestamp}.html`);
  const jsonReportFile = path.join(CONFIG.reportsDir, `eslint-report-${timestamp}.json`);

  // Run ESLint with multiple output formats
  const eslintResult = runCommand(`npx eslint . --ext .js --format json --output-file ${jsonReportFile}`, { silent: true });
  const htmlResult = runCommand(`npx eslint . --ext .js --format html --output-file ${reportFile}`, { silent: true });

  let issueCount = 0;
  let errorCount = 0;
  let warningCount = 0;

  if (fs.existsSync(jsonReportFile)) {
    try {
      const eslintData = JSON.parse(fs.readFileSync(jsonReportFile, 'utf8'));
      issueCount = eslintData.reduce((total, file) => total + file.messages.length, 0);
      errorCount = eslintData.reduce((total, file) => 
        total + file.messages.filter(msg => msg.severity === 2).length, 0);
      warningCount = eslintData.reduce((total, file) => 
        total + file.messages.filter(msg => msg.severity === 1).length, 0);
    } catch (error) {
      log(`Failed to parse ESLint JSON report: ${error.message}`, 'warning');
    }
  }

  const result = {
    success: eslintResult.success && errorCount === 0,
    issueCount,
    errorCount,
    warningCount,
    reportFile,
    jsonReportFile
  };

  if (result.success) {
    log(`ESLint check passed! ${warningCount} warnings found.`, 'success');
  } else {
    log(`ESLint check failed! ${errorCount} errors, ${warningCount} warnings found.`, 'error');
  }

  if (issueCount > CONFIG.maxIssues.eslint) {
    log(`Too many ESLint issues (${issueCount} > ${CONFIG.maxIssues.eslint})`, 'warning');
  }

  return result;
};

const runPrettierCheck = () => {
  log('Running Prettier format check...', 'info');

  const result = runCommand('npx prettier --check .', { silent: true });

  if (result.success) {
    log('Prettier check passed! All files are properly formatted.', 'success');
  } else {
    log('Prettier check failed! Some files need formatting.', 'error');
    log('Run "npm run format" to fix formatting issues.', 'info');
  }

  return result;
};

const runSecurityAudit = () => {
  log('Running security audit...', 'info');

  const timestamp = generateTimestamp();
  const reportFile = path.join(CONFIG.reportsDir, `security-audit-${timestamp}.json`);

  // Run npm audit
  const auditResult = runCommand(`npm audit --json > ${reportFile}`, { silent: true });

  let vulnerabilityCount = 0;
  let criticalCount = 0;
  let highCount = 0;
  let moderateCount = 0;
  let lowCount = 0;

  if (fs.existsSync(reportFile)) {
    try {
      const auditData = JSON.parse(fs.readFileSync(reportFile, 'utf8'));
      if (auditData.metadata && auditData.metadata.vulnerabilities) {
        const vulns = auditData.metadata.vulnerabilities;
        criticalCount = vulns.critical || 0;
        highCount = vulns.high || 0;
        moderateCount = vulns.moderate || 0;
        lowCount = vulns.low || 0;
        vulnerabilityCount = criticalCount + highCount + moderateCount + lowCount;
      }
    } catch (error) {
      log(`Failed to parse security audit report: ${error.message}`, 'warning');
    }
  }

  const result = {
    success: criticalCount === 0 && highCount === 0,
    vulnerabilityCount,
    criticalCount,
    highCount,
    moderateCount,
    lowCount,
    reportFile
  };

  if (result.success) {
    log(`Security audit passed! ${vulnerabilityCount} total vulnerabilities (${moderateCount} moderate, ${lowCount} low).`, 'success');
  } else {
    log(`Security audit failed! ${criticalCount} critical, ${highCount} high vulnerabilities found.`, 'error');
  }

  if (vulnerabilityCount > CONFIG.maxIssues.security) {
    log(`High number of vulnerabilities found (${vulnerabilityCount})`, 'warning');
  }

  return result;
};

const runTestCoverage = () => {
  log('Running test coverage analysis...', 'info');

  const result = runCommand('npm run test:coverage', { silent: true });

  // Parse coverage from output (simplified)
  let coveragePercentage = 0;
  if (result.output) {
    const coverageMatch = result.output.match(/All files[^|]*\|[^|]*\|[^|]*\|[^|]*\|[^|]*(\d+\.?\d*)/);
    if (coverageMatch) {
      coveragePercentage = parseFloat(coverageMatch[1]);
    }
  }

  const coverageResult = {
    success: result.success && coveragePercentage >= CONFIG.maxIssues.coverage,
    coveragePercentage
  };

  if (coverageResult.success) {
    log(`Test coverage passed! ${coveragePercentage}% coverage achieved.`, 'success');
  } else {
    log(`Test coverage insufficient! ${coveragePercentage}% < ${CONFIG.maxIssues.coverage}%`, 'error');
  }

  return coverageResult;
};

const generateSummaryReport = (results) => {
  const timestamp = generateTimestamp();
  const summaryFile = path.join(CONFIG.reportsDir, `quality-summary-${timestamp}.json`);

  const summary = {
    timestamp: new Date().toISOString(),
    overall: {
      success: Object.values(results).every(r => r.success),
      totalIssues: (results.eslint?.issueCount || 0) + (results.security?.vulnerabilityCount || 0)
    },
    eslint: results.eslint,
    prettier: results.prettier,
    security: results.security,
    coverage: results.coverage,
    recommendations: []
  };

  // Generate recommendations
  if (!results.eslint?.success) {
    summary.recommendations.push('Fix ESLint errors before deployment');
  }
  if (!results.prettier?.success) {
    summary.recommendations.push('Run "npm run format" to fix formatting issues');
  }
  if (!results.security?.success) {
    summary.recommendations.push('Address critical and high security vulnerabilities');
  }
  if (!results.coverage?.success) {
    summary.recommendations.push(`Increase test coverage to at least ${CONFIG.maxIssues.coverage}%`);
  }

  fs.writeFileSync(summaryFile, JSON.stringify(summary, null, 2));

  return { summary, summaryFile };
};

const printSummary = (summary) => {
  console.log('\n' + '='.repeat(60).cyan);
  console.log(' QUALITY CHECK SUMMARY'.cyan.bold);
  console.log('='.repeat(60).cyan);

  const status = summary.overall.success ? ' PASSED'.green : ' FAILED'.red;
  console.log(`Overall Status: ${status}`);
  console.log(`Total Issues: ${summary.overall.totalIssues}`);

  console.log('\n DETAILED RESULTS:'.cyan);
  console.log(`ESLint: ${summary.eslint?.success ? '' : ''} (${summary.eslint?.errorCount || 0} errors, ${summary.eslint?.warningCount || 0} warnings)`);
  console.log(`Prettier: ${summary.prettier?.success ? '' : ''} (formatting check)`);
  console.log(`Security: ${summary.security?.success ? '' : ''} (${summary.security?.criticalCount || 0} critical, ${summary.security?.highCount || 0} high)`);
  console.log(`Coverage: ${summary.coverage?.success ? '' : ''} (${summary.coverage?.coveragePercentage || 0}%)`);

  if (summary.recommendations.length > 0) {
    console.log('\n RECOMMENDATIONS:'.yellow);
    summary.recommendations.forEach(rec => console.log(`   ${rec}`.yellow));
  }

  console.log('\n REPORTS GENERATED:'.blue);
  if (summary.eslint?.reportFile) console.log(`   ESLint: ${summary.eslint.reportFile}`.blue);
  if (summary.security?.reportFile) console.log(`   Security: ${summary.security.reportFile}`.blue);
};

// Main execution
const main = async () => {
  console.log(' FloWorx Backend Quality Check'.cyan.bold);
  console.log('================================'.cyan);

  ensureReportsDir();

  const results = {};

  // Run all quality checks
  results.eslint = runESLintCheck();
  results.prettier = runPrettierCheck();
  results.security = runSecurityAudit();
  results.coverage = runTestCoverage();

  // Generate summary report
  const { summary, summaryFile } = generateSummaryReport(results);

  // Print summary
  printSummary(summary);

  console.log(`\n Summary report: ${summaryFile}`.blue);

  // Exit with appropriate code
  process.exit(summary.overall.success ? 0 : 1);
};

// Run if called directly
if (require.main === module) {
  main().catch(error => {
    log(`Fatal error: ${error.message}`, 'error');
    process.exit(1);
  });
}

module.exports = { main, runESLintCheck, runPrettierCheck, runSecurityAudit, runTestCoverage };

#!/usr/bin/env node

/**
 * Local Deployment Testing Script
 * Tests error handling and validation in local environment
 */

const axios = require('axios');
const colors = require('colors');

// Configuration
const BASE_URL = process.env.TEST_BASE_URL || 'http://localhost:5001';
const TIMEOUT = 10000;

// Test results tracking
let totalTests = 0;
let passedTests = 0;
let failedTests = 0;
const results = [];

// Utility functions
const log = (message, type = 'info') => {
  const timestamp = new Date().toISOString();
  switch (type) {
    case 'success':
      console.log(`[${timestamp}]  ${message}`.green);
      break;
    case 'error':
      console.log(`[${timestamp}]  ${message}`.red);
      break;
    case 'warning':
      console.log(`[${timestamp}]   ${message}`.yellow);
      break;
    case 'info':
    default:
      console.log(`[${timestamp}]   ${message}`.blue);
      break;
  }
};

const runTest = async (testName, testFunction) => {
  totalTests++;
  try {
    log(`Running: ${testName}`, 'info');
    await testFunction();
    passedTests++;
    results.push({ name: testName, status: 'PASS', error: null });
    log(`PASSED: ${testName}`, 'success');
  } catch (error) {
    failedTests++;
    results.push({ name: testName, status: 'FAIL', error: error.message });
    log(`FAILED: ${testName} - ${error.message}`, 'error');
  }
};

// Test functions
const testServerHealth = async () => {
  try {
    const response = await axios.get(`${BASE_URL}/health`, { timeout: TIMEOUT });
    if (response.status !== 200) {
      throw new Error(`Server health check failed: ${response.status}`);
    }
  } catch (error) {
    if (error.code === 'ECONNREFUSED') {
      throw new Error('Server is not running. Please start the server first.');
    }
    throw error;
  }
};

const testValidRegistration = async () => {
  const testData = {
    email: `test-${Date.now()}@example.com`,
    password: 'TestPass123!',
    firstName: 'John',
    lastName: 'Doe',
    companyName: 'Test Company Inc.'
  };

  const response = await axios.post(`${BASE_URL}/api/auth/register`, testData, { timeout: TIMEOUT });

  if (![201, 409].includes(response.status)) {
    throw new Error(`Unexpected status: ${response.status}`);
  }

  if (response.status === 409) {
    // User already exists - check error format
    const { data } = response;
    if (!data.success || !data.error || !data.error.type) {
      throw new Error('Error response format is incorrect');
    }
  }
};

const testInvalidEmail = async () => {
  const testData = {
    email: 'invalid-email',
    password: 'TestPass123!',
    firstName: 'John',
    lastName: 'Doe',
    companyName: 'Test Company'
  };

  try {
    const response = await axios.post(`${BASE_URL}/api/auth/register`, testData, { timeout: TIMEOUT });
    if (response.status !== 400) {
      throw new Error(`Expected 400, got ${response.status}`);
    }
  } catch (error) {
    if (error.response && error.response.status === 400) {
      const { data } = error.response;
      if (data.error?.type !== 'VALIDATION_ERROR') {
        throw new Error('Validation error type incorrect');
      }
      if (!Array.isArray(data.error.details)) {
        throw new Error('Validation details should be an array');
      }
      return; // Test passed
    }
    throw error;
  }
};

const testWeakPassword = async () => {
  const testData = {
    email: 'test@example.com',
    password: '123',
    firstName: 'John',
    lastName: 'Doe',
    companyName: 'Test Company'
  };

  try {
    await axios.post(`${BASE_URL}/api/auth/register`, testData, { timeout: TIMEOUT });
    throw new Error('Weak password should be rejected');
  } catch (error) {
    if (error.response && error.response.status === 400) {
      const { data } = error.response;
      const hasPasswordError = data.error?.details?.some(d => 
        d.field === 'password' && d.message.includes('Password must be between 8 and 128 characters')
      );
      if (!hasPasswordError) {
        throw new Error('Password validation error not found');
      }
      return; // Test passed
    }
    throw error;
  }
};

const testDisposableEmail = async () => {
  const testData = {
    email: 'test@tempmail.org',
    password: 'TestPass123!',
    firstName: 'John',
    lastName: 'Doe',
    companyName: 'Test Company'
  };

  try {
    await axios.post(`${BASE_URL}/api/auth/register`, testData, { timeout: TIMEOUT });
    throw new Error('Disposable email should be rejected');
  } catch (error) {
    if (error.response && error.response.status === 400) {
      const { data } = error.response;
      const hasDisposableError = data.error?.details?.some(d => 
        d.message.includes('Disposable email addresses are not allowed')
      );
      if (!hasDisposableError) {
        throw new Error('Disposable email validation error not found');
      }
      return; // Test passed
    }
    throw error;
  }
};

const testXSSProtection = async () => {
  const testData = {
    email: 'test@example.com',
    password: 'TestPass123!',
    firstName: '<script>alert("xss")</script>John',
    lastName: 'Doe<img src=x onerror=alert(1)>',
    companyName: 'Test Company'
  };

  const response = await axios.post(`${BASE_URL}/api/auth/register`, testData, { 
    timeout: TIMEOUT,
    validateStatus: () => true // Accept any status
  });

  if (response.status === 201) {
    // Check if XSS was sanitized
    if (response.data.user.firstName.includes('<script>') || 
        response.data.user.lastName.includes('<img')) {
      throw new Error('XSS content not sanitized');
    }
  } else if (response.status === 400) {
    // XSS rejected by validation - also acceptable
    if (response.data.error?.type !== 'VALIDATION_ERROR') {
      throw new Error('Expected validation error for XSS content');
    }
  } else {
    throw new Error(`Unexpected status for XSS test: ${response.status}`);
  }
};

const testSQLInjection = async () => {
  const testData = {
    email: "admin@example.com'; DROP TABLE users; --",
    password: "' OR '1'='1",
    firstName: 'John',
    lastName: 'Doe',
    companyName: 'Test Company'
  };

  try {
    await axios.post(`${BASE_URL}/api/auth/register`, testData, { timeout: TIMEOUT });
    throw new Error('SQL injection should be rejected');
  } catch (error) {
    if (error.response && error.response.status === 400) {
      const { data } = error.response;
      if (data.error?.type !== 'VALIDATION_ERROR') {
        throw new Error('Expected validation error for SQL injection');
      }
      return; // Test passed
    }
    throw error;
  }
};

const test404Handling = async () => {
  try {
    await axios.get(`${BASE_URL}/api/auth/non-existent-route`, { timeout: TIMEOUT });
    throw new Error('404 route should not exist');
  } catch (error) {
    if (error.response && error.response.status === 404) {
      const { data } = error.response;
      if (data.error?.type !== 'NOT_FOUND_ERROR') {
        throw new Error('404 error type incorrect');
      }
      if (data.error?.code !== 404) {
        throw new Error('404 error code incorrect');
      }
