// Insulation Business Intelligence Config Cache Manager
const clientId = $env.CLIENT_ID;
const configData = $input.first().json;
const cacheKey = `config:${clientId}:${configData.version}`;

const enhancedConfig = {
  ...configData,
  insulationIntelligence: {
    hasEmergencyService: configData.client.phones?.length > 1,
    serviceTypes: configData.services || ['blown_in_insulation', 'batt_insulation', 'spray_foam', 'radiant_barrier', 'air_sealing'],
    workingHours: configData.client.hours || {},
    priceRanges: configData.pricing || {},
    serviceArea: configData.client.service_area || 'Local area',
    managerNames: (configData.people.managers || []).map(m => m.name),
    supplierDomains: (configData.suppliers || []).flatMap(s => s.domains || []),
    specializations: ['residential', 'commercial', 'new_construction', 'retrofit'],
    emergencyKeywords: ['energy emergency', 'frozen pipes', 'urgent', 'immediate'],
    insulationTypes: {
      fiberglass: ['batt_insulation', 'blown_fiberglass', 'rigid_boards'],
      cellulose: ['blown_cellulose', 'dense_pack', 'wet_spray'],
      spray_foam: ['open_cell', 'closed_cell', 'two_part_foam'],
      specialty: ['radiant_barrier', 'reflective_insulation', 'mineral_wool']
    },
    applicationAreas: {
      attic: ['blown_in', 'batt_installation', 'air_sealing'],
      walls: ['dense_pack', 'spray_foam', 'batt_insulation'],
      basement: ['rim_joist', 'foundation_walls', 'crawl_space'],
      commercial: ['metal_buildings', 'warehouses', 'office_buildings']
    },
    energyBenefits: {
      heating_savings: '20-50% reduction in heating costs',
      cooling_savings: '10-30% reduction in cooling costs',
      comfort: 'improved temperature consistency',
      soundproofing: 'noise reduction between rooms'
    },
    seasonalConsiderations: {
      winter: ['heating_efficiency', 'frozen_pipe_prevention', 'ice_dam_prevention'],
      summer: ['cooling_efficiency', 'attic_heat_reduction'],
      year_round: ['energy_savings', 'comfort_improvement', 'sound_dampening']
    },
    safetyProtocols: ['respiratory_protection', 'skin_protection', 'confined_space_safety']
  }
};

$setStaticData(cacheKey, enhancedConfig);
return [{ json: { ...enhancedConfig, cached: false, cacheKey: cacheKey } }];
