// Enhanced Config Cache Manager with business intelligence preservation
const clientId = $env.CLIENT_ID;
const configData = $input.first().json;
const cacheKey = `config:${clientId}:${configData.version}`;

// Get existing cached config
const existingConfig = $getStaticData(cacheKey);

if (existingConfig && existingConfig.version === configData.version) {
  // Use cached config
  return [{
    json: {
      ...configData,
      cached: true,
      cacheKey: cacheKey,
      businessIntelligence: {
        hasEmergencyService: configData.client.phones?.length > 1,
        serviceTypes: configData.services || ['installation', 'repair', 'maintenance'],
        workingHours: configData.client.hours || {},
        priceRanges: configData.pricing || {},
        serviceArea: configData.client.service_area || 'Local area'
      }
    }
  }];
} else {
  // Cache new config and enhance with business intelligence
  const enhancedConfig = {
    ...configData,
    businessIntelligence: {
      hasEmergencyService: configData.client.phones?.length > 1,
      serviceTypes: configData.services || ['installation', 'repair', 'maintenance'],
      workingHours: configData.client.hours || {},
      priceRanges: configData.pricing || {},
      serviceArea: configData.client.service_area || 'Local area',
      managerNames: (configData.people.managers || []).map(m => m.name),
      supplierDomains: (configData.suppliers || []).flatMap(s => s.domains || [])
    }
  };

  $setStaticData(cacheKey, enhancedConfig);

  // Clear old cache entries for this client
  const staticData = $getStaticData();
  const oldKeys = Object.keys(staticData).filter(key => 
    key.startsWith(`config:${clientId}:`) && key !== cacheKey
  );

  oldKeys.forEach(key => {
    $setStaticData(key, undefined);
  });

  return [{
    json: {
      ...enhancedConfig,
      cached: false,
      cacheKey: cacheKey
    }
  }];
}

