// Business-Intelligent Signature Processor for Service Businesses
const configData = $('Enhanced Config Cache').first().json;
const emailData = $('Gmail Trigger').first().json;
const classificationData = $json;
const businessContext = classificationData.parsed_output.business_context || {};

// Determine signature based on mode and business context
let signature = '';
const signatureMode = configData.signature?.mode || 'default';

// Business-specific signature logic
switch (signatureMode) {
  case 'default':
    // Enhanced default signature with business intelligence
    const emergencyPhone = configData.businessIntelligence.hasEmergencyService ? 
      `\nEmergency: ${configData.client.phones?.[1] || configData.client.phones?.[0]}` : '';

    signature = `\n\nBest regards,\nThe ${configData.client.name || 'Service'} Team\n${configData.client.name || 'Your Service Business'}\nPhone: ${configData.client.phones?.[0] || 'Contact us'}${emergencyPhone}\n${configData.client.website || ''}\n\nService Area: ${configData.businessIntelligence.serviceArea}\nServices: ${configData.businessIntelligence.serviceTypes.join(', ')}`;
    break;

  case 'custom':
    // Use custom signature text
    signature = configData.signature.custom_text ? `\n\n${configData.signature.custom_text}` : '';
    break;

  case 'none':
    // No signature
    signature = '';
    break;

  default:
    signature = `\n\nBest regards,\nThe ${configData.client.name || 'Service'} Team`;
}

// Enhanced business context for AI drafting
const enhancedContext = {
  ...businessContext,
  workingHours: configData.businessIntelligence.workingHours,
  hasEmergencyService: configData.businessIntelligence.hasEmergencyService,
  serviceTypes: configData.businessIntelligence.serviceTypes,
  priceRanges: configData.businessIntelligence.priceRanges,
  managerNames: configData.businessIntelligence.managerNames
};

// Prepare comprehensive data for draft generation
return [{
  json: {
    ...classificationData,
    config: configData,
    email: emailData,
    signature: signature,
    signatureMode: signatureMode,
    signatureInstruction: signatureMode === 'none' ? 'do NOT append signature' : 'append the provided signature',
    businessContext: enhancedContext,
    draftingInstructions: {
      urgencyLevel: classificationData.parsed_output.urgency_level,
      responseTime: classificationData.parsed_output.estimated_response_time,
      requiresManager: enhancedContext.requires_manager,
      isEmergency: enhancedContext.is_emergency,
      serviceType: enhancedContext.service_type
    }
  }
}];

